from flask import Flask, render_template, request, redirect
app = Flask(__name__)

pokemons = [
    ['Refrigerante', 'data:image/jpeg;base64,'],
    ['Lanche', 'https://emc.acidadeon.com/dbimagens/lanches_como_790x505_06032018153232.jpg'],
    ['Pizza', 'https://img.stpu.com.br/?img=https://s3.amazonaws.com/pu-mgr/default/a0R0f000010xA7GEAU/5c489cd8e4b0842c9b1b6c10.jpg&w=710&h=462'],
    ['Suco', 'https://www.hipolabor.com.br/blog/wp-content/uploads/2017/07/103783-5-sucos-que-fazem-bem-para-a-sua-saude.jpg']
]

@app.route('/')
def index():
    return render_template(
        'index.html',
        titulo='PRODUTOS',
        pokemons=pokemons
    )

@app.route('/poke/<int:id>')
def pokemon(id):
    poke = pokemons[id]
    return render_template(
        'pokemon.html',
        pokemon=poke,
        id=id
    )

@app.route('/add')
def add():
    return render_template('add.html')

@app.route('/store', methods=['POST'])
def store():
    nome = request.form['nome']
    img = request.form['img']

    pokemons.append([nome, img])
    return redirect('/')

@app.route('/delete/<int:id>')
def delete(id):
    del pokemons[id]
    return redirect('/')

if __name__ == '__main__':
    app.run()